public class SecureFileUpload {
    public void uploadFile(String fileName, String fileContent) {
        try {
            // Validate file name against allowed patterns
            if (!fileName.matches("[a-zA-Z0-9._-]+\\.txt")) {
                throw new SecurityException("Invalid file name");
            }

            // Use safe path construction with user input sanitization
            Path safePath = Paths.get("uploads/", fileName).normalize();
            if (!safePath.startsWith("uploads/")) {
                throw new SecurityException("Path traversal attempt detected");
            }

            Files.write(safePath, fileContent.getBytes());
        } catch (IOException | SecurityException e) {
            e.printStackTrace();
        }
