// SECURE: This code properly validates and restricts file access
public class SecureFileReaderExample {
    private static final String CONFIG_DIR = "/app/config/";

    public static String readConfigFile(String filename) {
        // Validate filename doesn't contain path traversal characters
        if (filename.contains("..") || filename.contains("/") || filename.contains("\\")) {
            return "Invalid filename";
        }

        // Normalize and restrict to config directory
        Path safePath = Paths.get(CONFIG_DIR, filename).normalize();

        // Ensure the resolved path is still within our allowed directory
        if (!safePath.startsWith(Paths.get(CONFIG_DIR).normalize())) {
            return "Access denied";
        }

        try {
            return new String(Files.readAllBytes(safePath));
        } catch (IOException e) {
            return "Error reading file";
        }
    }

    public static void main(String[] args) {
        // Input is now safely validated and restricted
        String content = readConfigFile(args[0]);
        System.out.println(content);
    }
}
