// GOOD: use PreparedStatement to avoid SQL injection
// This servlet safely handles the request parameter with a parameterized query.
protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
    String userId = request.getParameter("id");               // user‚Äëcontrolled input
    Connection conn = null;
    PreparedStatement pstmt = null;
    ResultSet rs = null;
    try {
        conn = dataSource.getConnection();
        // Safe: the query is pre‚Äëcompiled and the user input is bound as a parameter
        String sql = "SELECT username, email FROM users WHERE id = ?";
        pstmt = conn.prepareStatement(sql);
        pstmt.setInt(1, Integer.parseInt(userId));            // validate / convert input
        rs = pstmt.executeQuery();
        // process result set ...
    } catch (NumberFormatException e) {
        // handle invalid id format
        response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Invalid user ID");
    } finally {
        if (rs != null) rs.close();
        if (pstmt != null) pstmt.close();
        if (conn != null) conn.close();
    }
}
