import java.sql.DriverManager;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

public class UserServlet extends HttpServlet {
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException {
        // Get user input and validate/parse it safely
        String userIdParam = request.getParameter("id");
        int userId;
        try {
            userId = Integer.parseInt(userIdParam);
        } catch (NumberFormatException nfe) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Invalid user id");
            return;
        }

        // Use a PreparedStatement with a parameter placeholder to avoid injection
        String sql = "SELECT * FROM users WHERE id = ?";
        Connection conn = null;
        PreparedStatement pstmt = null;
        ResultSet rs = null;
        try {
            // Obtain DB connection (details omitted)
            conn = DriverManager.getConnection("jdbc:mysql://localhost:3306/app", "user", "pass");
            pstmt = conn.prepareStatement(sql);
            pstmt.setInt(1, userId); // safely bind the integer parameter
            rs = pstmt.executeQuery(); // safe execution

            // Process the result set (omitted for brevity)
        } catch (Exception e) {
            throw new IOException("Database error", e);
        } finally {
            // Clean up resources
            try { if (rs != null) rs.close(); } catch (Exception ignored) {}
            try { if (pstmt != null) pstmt.close(); } catch (Exception ignored) {}
            try { if (conn != null) conn.close(); } catch (Exception ignored) {}
