// Secure example ‚Äì proper validation of the file path using java.io.File API
// This version prevents path traversal by resolving the canonical path and ensuring
// the requested file resides within the allowed directory.
import java.io.*;
import javax.servlet.*;
import javax.servlet.http.*;

public class SafeFileDownloadServlet extends HttpServlet {
    private static final File BASE_DIR = new File("/var/www/files").getCanonicalFile();

    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        String fileName = request.getParameter("file");
        if (fileName == null) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Missing file parameter");
            return;
        }

        // Resolve the requested file against the base directory
        File requestedFile = new File(BASE_DIR, fileName).getCanonicalFile();

        // Verify that the resolved path is still within BASE_DIR
        if (!requestedFile.getPath().startsWith(BASE_DIR.getPath())) {
            // Attempted path traversal ‚Äì reject the request
            response.sendError(HttpServletResponse.SC_FORBIDDEN, "Invalid file path");
            return;
        }

        // Serve the file only if it exists and is a regular file
        if (requestedFile.isFile()) {
            response.setContentType("application/octet-stream");
            response.setHeader("Content-Disposition",
                               "attachment; filename=\"" + requestedFile.getName() + "\"");
            try (FileInputStream fis = new FileInputStream(requestedFile);
                 OutputStream out = response.getOutputStream()) {
                byte[] buffer = new byte[8192];
                int bytesRead;
                while ((bytesRead = fis.read(buffer)) != -1) {
                    out.write(buffer, 0, bytesRead);
                }
            }
        } else {
            response.sendError(HttpServletResponse.SC_NOT_FOUND, "File not found");
        }
    }
}
