// Fixed version using parameterized queries to prevent SQL injection
public class SecureUserDAO {
    public boolean authenticate(String username, String password) {
        Connection conn = null;
        PreparedStatement stmt = null;
        try {
            conn = DriverManager.getConnection("jdbc:mysql://localhost/test");

            // SECURE: Using PreparedStatement with parameter placeholders
            String sql = "SELECT * FROM users WHERE username = ? AND password = ?";

            stmt = conn.prepareStatement(sql);
            stmt.setString(1, username); // Parameters are properly escaped
            stmt.setString(2, password);

